clear all, close all,clc

ww= xlsread('New_SWIR_wavelength.xlsx');
%white read
input_file2 = 'white'; 
headfile2 = strcat(input_file2, '.hdr'); 
imfile2 = strcat(input_file2, '.img'); 

% Read White head file
fid2=fopen(headfile2, 'r');
c2=textscan(fid2, '%s %s %s');
fclose(fid2);

% Read White samples, lines, and bands number of headfile
samples = str2double(char(c2{3}(4)));
lines = str2double(char(c2{3}(5)));
bands = str2double(char(c2{3}(6)));

% White average
[fid2,msg2] = fopen(imfile2,'r');
[NormalData2, count2]= fread(fid2, [samples, lines*bands], 'uint16');
fclose(fid2);


I = zeros(samples, lines, bands);% first make all zero
II = zeros(samples, lines, bands);
BandImage = zeros(samples, lines);

% white image's each wavelength intensity graph
for ib = 1:bands
    for il = 1:lines
         BandImage(:,lines-il+1) = NormalData2(:,(il-1)*bands + ib);
     end
     I(:,:,ib) = BandImage;
end

whitebalance = mean(I,2);
www = zeros(samples, 450, bands);
for i = 1:450
    www(:,i,:) = whitebalance;
end

%dark
%% dark read
input_file3 = 'dark';
headfile3 = strcat(input_file3, '.hdr');
imfile3 = strcat(input_file3, '.img');

% Read Black head file
fid3=fopen(headfile3, 'r');
c3=textscan(fid3, '%s %s %s');
fclose(fid3);

% Read Black samples, lines, and bands number of headfile
samples = str2double(char(c3{3}(4)));
lines = str2double(char(c3{3}(5)));
bands = str2double(char(c3{3}(6)));

% Black average
[fid3,msg3] = fopen(imfile3,'r');
[NormalData3, count3]= fread(fid3, [samples, lines*bands], 'uint16');
fclose(fid3);


% Black image's each wavelength intensity graph
for ib = 1:bands
    for il = 1:lines
         BandImage(:,lines-il+1) = NormalData3(:,(il-1)*bands + ib);
     end
     II(:,:,ib) = BandImage;
end

blackbalance = mean(II,2);
bbb = zeros(samples, lines, bands);
 
for i = 1:450 %put intensity
    bbb(:,i,:) = blackbalance;
end

%sample
input_file1 = 'plate1'; %chamge here for next sample
headfile1 = strcat(input_file1, '.hdr'); 
imfile1 = strcat(input_file1, '.img'); 

fid=fopen(headfile1);
c=textscan(fid, '%s %s %s');
fclose(fid);

% Read samples, lines, and bands number of headfile
     samples = str2double(char(c{3}(4)));
     lines = str2double(char(c{3}(5)));
     bands = str2double(char(c{3}(6)));

% Wavelength, Samples, Line, Band counting, 
     [fid1,msg1] = fopen(imfile1,'r');
     [NormalData, count]= fread(fid1, [samples, lines*bands], 'uint16');
     fclose(fid1);

% 3-d and 2-d variables to storehold the image
     I = zeros(samples, lines, bands); 
     BandImage = zeros(samples, lines);
     minus = zeros(samples, lines, bands);
     nII = zeros(samples, lines, bands); 


     
% image show in each wavebands
for ib = 1:bands
    for il = 1:lines
        BandImage(:,lines-il+1) = NormalData(:,(il-1)*bands + ib);

    end
%          I2(:,:,ib) = BandImage;
         %P(:,:,ib) = BandImage;
%          Betaimage= BandImage*beta(ib+1,1);
         I(:,:,ib) = BandImage;
         nII(:,:,ib)= (I(:,:,ib) - bbb(:,:,ib) +0.0001)./www(:,:,ib);% reflectance equation 0.0001 means it calculate 0 also 
         %nII(:,:,ib)= (I(:,:,ib));
%          figure(88), imshow(nII(:,:,ib));
end


% original HSI image
aa= nII(:,:,30);
figure(23); imshow(aa);

for b=1:bands
    mm1(:,:,b)=medfilt2(nII(:,:,b),[3 3]);    
end
% Msize=9;

% aa1= mm1(:,:,30);
% figure(); imshow(aa1);

re_nII = reshape(mm1, samples*lines, bands);
pr_nII = Mean_normalize(re_nII);
mm1 = reshape(pr_nII, samples, lines, bands);
% ss= aa(1:270,50:739);

% masking 
no_black = aa>0.3;
no_black1 = imerode(no_black, strel('disk', 1));
figure(22),imshow(no_black);


% beta from PLS model
beta1=xlsread('beta.xlsx');
beta=beta';
for ib = 1:bands
     BandImage=mm1(:,:,ib);
     Betaimage=BandImage*beta(1,ib+1); %pc_number
     PP(:,:,ib) = Betaimage;
end

PLS_image=beta(1,1)+sum(PP,3);

[xxm yym]=find(PLS_image == Inf);
leng_xxm = length(xxm);
for ixm = 1:leng_xxm
    PLS_image(xxm(ixm), yym(ixm)) = 0;
end


%% PLS image after masking 
F=PLS_image.*no_black1;

